// src/components/Chatbot.tsx

import React, { useState } from 'react';
import axios from 'axios';
import { useTranslation } from 'react-i18next';

const Chatbot: React.FC = () => {
  const { t, i18n } = useTranslation();
  const [patientId, setPatientId] = useState('');
  const [message, setMessage] = useState('');
  const [conversation, setConversation] = useState<{ role: string; content: string }[]>([]);
  const [response, setResponse] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSendMessage = async () => {
    if (!patientId || !message) {
      setError(t('fill_required_fields'));
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      const payload = {
        patient_id: patientId,
        message: message,
        language: i18n.language,
      };

      const res = await axios.post(`${process.env.REACT_APP_BACKEND_URL}/chatbot`, payload, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      setConversation([...conversation, { role: 'user', content: message }, { role: 'assistant', content: res.data.response }]);
      setMessage('');
      setResponse(res.data.response);
    } catch (err: any) {
      console.error('Chatbot Error:', err);
      setError(t('error_generating_response'));
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="bg-white bg-opacity-80 shadow-lg rounded-lg p-6 backdrop-blur-md">
      <h2 className="text-2xl font-semibold mb-4 text-indigo-600">{t('ai_doctor_chatbot')}</h2>

      {/* Patient ID Input */}
      <div className="mb-4">
        <label htmlFor="patientId" className="block text-sm font-medium text-gray-700 mb-1">
          {t('patient_id')}
        </label>
        <input
          type="text"
          id="patientId"
          value={patientId}
          onChange={(e) => setPatientId(e.target.value)}
          className="w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
          placeholder={t('enter_your_patient_id')}
          required
        />
      </div>

      {/* Conversation History */}
      <div className="mb-4 h-64 overflow-y-auto border rounded p-4 bg-gray-50">
        {conversation.map((msg, index) => (
          <div key={index} className={`mb-2 ${msg.role === 'assistant' ? 'text-indigo-700' : 'text-gray-800'}`}>
            <strong>{msg.role === 'assistant' ? t('assistant') : t('you')}:</strong> {msg.content}
          </div>
        ))}
      </div>

      {/* Message Input */}
      <div className="flex items-center">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="flex-grow rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
          placeholder={t('type_your_message')}
        />
        <button
          type="button"
          onClick={handleSendMessage}
          disabled={isLoading}
          className="ml-2 px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 disabled:opacity-50"
        >
          {isLoading ? t('sending') : t('send')}
        </button>
      </div>

      {error && <p className="mt-2 text-red-600">{error}</p>}
    </div>
  );
};

export default Chatbot;
