import React, { useState } from 'react';
import axios from 'axios';

const Diagnosis: React.FC = () => {
  const [image, setImage] = useState<File | null>(null);
  const [patientData, setPatientData] = useState('');
  const [diagnosis, setDiagnosis] = useState('');
  const [audioSrc, setAudioSrc] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setImage(e.target.files[0]);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!image || !patientData) {
      setError('Please provide both an image and patient data.');
      return;
    }

    setIsLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('image', image);
    formData.append('patient_data', patientData);

    try {
      const response = await axios.post(`${process.env.REACT_APP_BACKEND_URL}/diagnosis`, formData);
      setDiagnosis(response.data.diagnosis);
      if (response.data.audio) {
        setAudioSrc(`data:audio/mp3;base64,${response.data.audio}`);
      }
    } catch (error) {
      setError('An error occurred while processing your request. Please try again.');
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="bg-white shadow-md rounded-lg p-6">
      <h2 className="text-2xl font-semibold mb-4">Multimodal Medical Diagnosis</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="image" className="block text-sm font-medium text-gray-700">Upload Chest X-ray Image</label>
          <input
            type="file"
            id="image"
            accept="image/jpeg,image/png"
            onChange={handleImageChange}
            className="mt-1 block w-full text-sm text-gray-500
                      file:mr-4 file:py-2 file:px-4
                      file:rounded-full file:border-0
                      file:text-sm file:font-semibold
                      file:bg-blue-50 file:text-blue-700
                      hover:file:bg-blue-100"
          />
        </div>
        <div>
          <label htmlFor="patientData" className="block text-sm font-medium text-gray-700">Patient Data</label>
          <textarea
            id="patientData"
            value={patientData}
            onChange={(e) => setPatientData(e.target.value)}
            rows={4}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
            placeholder="Enter patient symptoms and medical history"
          ></textarea>
        </div>
        <button
          type="submit"
          disabled={isLoading}
          className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
        >
          {isLoading ? 'Processing...' : 'Get Diagnosis'}
        </button>
      </form>
      {error && <p className="mt-4 text-red-600">{error}</p>}
      {diagnosis && (
        <div className="mt-6">
          <h3 className="text-lg font-medium text-gray-900">Diagnosis:</h3>
          <p className="mt-2 text-gray-600">{diagnosis}</p>
          {audioSrc && (
            <div className="mt-4">
              <h4 className="text-md font-medium text-gray-900">Audio Diagnosis:</h4>
              <audio controls src={audioSrc} className="mt-2 w-full">
                Your browser does not support the audio element.
              </audio>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Diagnosis;
